mapper.select:
select cl.id as id, cl.title as title, cl.field as field, cl.align as align, dd.name as alignName from columns cl inner join data_dictionary dd on cl.align = dd.value
cl.is_del = 0
and cl.menu_id = #{menuId}
and cl.align = #{align}
and cl.field like CONCAT(#{field},'%')

select id as id, field as field, title as title, align as align, menu_id as menuId, is_show as isShow from columns
is_del = 0
AND is_show = #{isShow}
AND menu_id = #{menuId}

mapper.update:
update columns set is_del = 1

  id in

    #{item}
