mapper.select:
select ob.id as id, ob.field as field, ob.html_type as htmlType, ob.text_name as textName, dd.name as htmlTypeName from opera_bo ob inner join data_dictionary dd on ob.html_type = dd.value
ob.is_del = 0
and ob.menu_id = #{menuId}
and ob.html_type = #{htmlType}

select id as id, text_name as textName, html_type as htmlType, field as field, is_show as isShow, menu_id as menuId from opera_bo
is_del = 0
AND is_show = #{isShow}
AND menu_id = #{menuId}

mapper.update:
update opera_bo set is_del = 1

